export function removePost(index){
    return {
        type: 'REMOVE_POST',
        index
    }
}

export function addPost(post){
    return {
        type: 'ADD_POST',
        post
    }
}

export function addComment(comment, postId){
    console.log(comment)
    console.log(postId)
    return {
        type: 'ADD_COMMENT',
        comment,
        postId
    }
}
