import posts from '../data/posts';
import { combineReducers } from 'redux';

function postsReducer(state=posts, action){
    switch (action.type){
        case 'REMOVE_POST': return [...state.posts.slice(0,action.index), ...state.posts.slice(action.index + 1)]
        case 'ADD_POST': return [...state.posts, action.post]
        default : return state.posts

    }
}

function commentsReducer(state=[], action){
    switch (action.type){
        default : return state
    }

}

const rootReducer = combineReducers({postsReducer,commentsReducer})

export default rootReducer
