import React, {Component} from 'react';
import Photowall from './Photowall';
import Title from './Title';
import AddPhoto from './AddPhoto';
import {Route} from 'react-router-dom';

class Main extends Component {

    constructor(){
        //console.log("Inside constructor");
        super();
        this.state = {
            posts : [],
            //screen : 'photos'
        };
        this.removePhoto = this.removePhoto.bind(this);
        //this.navigate = this.navigate.bind(this);
    }

    removePhoto(postRemoved){
        //super();
        //console.log("Inside removePhoto");
        //console.log(postRemoved.description);
        this.setState((state) => ({
            posts: state.posts.filter(post => post !== postRemoved)
        }));

        // this.state.posts.filter((post) => {
        //     return post.id = postRemoved.id;
        // });
    }

    addPhoto(postSubmitted){
        console.log("Inside addPhoto");
        this.setState((state) => ({
            posts: state.posts.concat([postSubmitted])
        }));
    }

    // navigate(){
    //     console.log("Inside navigate");
    //     this.setState({
    //         screen: 'addPhoto'
    //     });
    // }

    componentDidMount(){
        //console.log("Inside componentDidMount");
        const data = simulatefetchFromDB();
        // this.setState((state) => ({
        //     posts: data
        // }));
        this.setState({
            posts : data
        })

    }

    componentDidUpdate(){
        //console.log("Inside componentDidUpdate");
    }

    render(){
      //console.log("Inside render");  

      return ( 
      <div>
        <Route exact path="/" render={() => (
            <div>
                <Title titleText="Photowall App"/>  
                {/* <Photowall posts = {this.state.posts} onRemovePhoto={this.removePhoto} onNavigate={this.navigate}/> */}
                <Photowall posts = {this.state.posts} onRemovePhoto={this.removePhoto}/>
            </div>
        )}/>
        {/* <Route path="/AddPhoto" component={AddPhoto}/> */}
        <Route exact path="/AddPhoto" render={(history) => (
            <AddPhoto onAddPhoto={(addedPost) => {
                  console.log(addedPost);
                  this.addPhoto(addedPost);  
                  //history.push('/');
                  this.props.history.push('/');
                  //this.context.history.push('/');
            }}/>
        )}/>
      </div> 
      )
    }
  }

function simulatefetchFromDB(){
    //console.log("Inside simulatefetchFromDB");
    return [{
        id: 0,
        description: "Beautiful Landscape",
        imageLink: "https://image.jimcdn.com/app/cms/image/transf/none/path/sa6549607c78f5c11/image/i4eeacaa2dbf12d6d/version/1490299332/most-beautiful-landscapes-in-europe-lofoten-european-best-destinations-copyright-iakov-kalinin.jpg" +
        "3919321_1443393332_n.jpg"
        }, {
        id: 1,
        description: "Aliens???",
        imageLink: "https://img.purch.com/rc/640x415/aHR0cDovL3d3dy5zcGFjZS5jb20vaW1hZ2VzL2kvMDAwLzA3Mi84NTEvb3JpZ2luYWwvc3BhY2V4LWlyaWRpdW00LWxhdW5jaC10YXJpcS1tYWxpay5qcGc=" +
        "08323785_735653395_n.jpg"
        }, {
        id: 2,
        description: "On a vacation!",
        imageLink: "https://fm.cnbc.com/applications/cnbc.com/resources/img/editorial/2017/08/24/104670887-VacationExplainsTHUMBWEB.1910x1000.jpg"
        }];
}

export default Main;
